// Create this implementation as a singleton.
[PartCreationPolicy(CreationPolicy.Shared)]
public class ValidationErrorProcessor : IValidationErrorNotification
{
    // Let the UI know that a validation error occurred.
    public OnValidationError(VerifierResultCollection validationErrors)
    {
        EventFns.Publish(new HandleValidationErrors(validationErrors));
    }
}

public OperationResult GetCustomers(Action<IEnumerable<Customer>> onSuccess = null,
                Action<Exception> onFail = null)
                {
                var op = Manager.Customers.ExecuteAsync();
                return op.OnComplete(onSuccess, onFail).AsOperationResult();
                }

public OperationResult GetCustomerCount(Action<int> onSuccess = null,
                Action<Exception> onFail = null)
                {
                var op = Manager.Customers.AsScalarAsync().Count();
                return op.OnComplete(onSuccess, onFail).AsOperationResult();
                }

public OperationResult Save(Action onSuccess = null, Action<Exception> onFail = null)
                {
                EntitySaveOperation op = Manager.SaveChangesAsync();
                return op.OnComplete(onSuccess, onFail).AsOperationResult();
                }

public OperationResult<SaveResult> Save(Action<SaveResult> onSuccess = null, Action<Exception> onFail = null)
                {
                EntitySaveOperation op = Manager.SaveChangesAsync();
                return op.OnComplete(onSuccess, onFail).AsOperationResult();
                }
